export default function validate (userData) {

    const regexEmail = /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i;

    const errors = {};

    if (!userData.email) errors.email = 'Email is required'
    else if (!regexEmail.test(userData.email)) errors.email = 'Email is invalid'
    else if(userData.email.length > 35) errors.email = 'Email is too long';

    const passwordRegex = /^(?=.*[0-9])/;

    if (!userData.password) errors.password = 'Password is required'
    else if (passwordRegex.test(userData.password)) 
    errors.password = 'Password require at least one number'
    else if (userData.password.length < 6 || userData.password.lenght > 10) 
    errors.password = 'Password should be at least 6 characters long and less than 10 characters long'

    return errors;
};
